graphql.query:
query($id: ID!) {
    collection(id: $id) {
        id
        name
        description
        address
        website
        image_url
    }
}

query($network_id: ID!, $contract: Address!) {
    collection_by_address(network_id: $network_id, contract: $contract) {
        id
        name
        description
        address
        website
        image_url
    }
}

query($id: ID!) {
    collection(id: $id) {
